   $(".confirmSkip").click(function(e) {
                e.stopImmediatePropagation();
                url = $(this).attr('href');
                Swal.fire({
                    title: "{{trans('messages.admin_common_Are_you_sure')}}",
                    text: "{{trans('messages.admin_Want_to_end_the_destination')}}",
                    icon: "warning",
                    showCancelButton: true,
                    confirmButtonText: "{{trans('messages.admin_Yes_end_it')}}",
                    cancelButtonText: "{{trans('messages.admin_No_cancel')}}",
                    reverseButtons: true
                }).then(function(result) {
                    if (result.value) {
                        window.location.replace(url);
                    } else if (result.dismiss === "cancel") {
                        Swal.fire(
                            "{{trans('messages.admin_common_Cancelled')}}",
                            "{{trans('messages.admin_Your_imaginary_file_is_safe')}}",
                            "{{trans('messages.admin_common_error')}}"
                        )
                    }
                });
                e.preventDefault();
            });
</script>

